void read_from_string(const char * s,  siena_impl::ibitvector & v) {
    for(size_t pos = 0; *s != 0; ++s) {
	switch (*s) {
	case '1': v.set(pos, Mem);
	case '0': ++pos; 
	    break;
	default: 
	    return;
	}
    }
}

string & write_into_string(string & s, const siena_impl::bitvector & v) {
    s.clear();
    for(size_t pos = 0; pos < v.get_size(); ++pos)
	s.push_back(v.test(pos) ? '1' : '0');
    return s;
}

string & write_into_string(string & s, const siena_impl::ibitvector & v) {
    s.clear();
    for(size_t pos = 0; pos < v.get_size(); ++pos)
	s.push_back(v.test(pos) ? '1' : '0');
    return s;
}

void run_test_case(int i) {    
    const TestCase & t = TestCases[i];

    siena_impl::ibitvector x;
    siena_impl::ibitvector y;
    string buf;

    read_from_string(t.Xin, x);
    read_from_string(t.Yin, y);

    BOOST_CHECK_EQUAL(x.get_size(), t.Xs);
    BOOST_CHECK_EQUAL(x.get_count(), t.Xc);
    BOOST_CHECK_EQUAL(write_into_string(buf, x), t.X);

    BOOST_CHECK_EQUAL(y.get_size(), t.Ys);
    BOOST_CHECK_EQUAL(y.get_count(), t.Yc);
    BOOST_CHECK_EQUAL(write_into_string(buf, y), t.Y);

    siena_impl::bitvector res(x.get_size() > y.get_size() ? x.get_size() : y.get_size());

    res.set(x);

    BOOST_CHECK_EQUAL(res.get_size(), t.RXs);
    BOOST_CHECK_EQUAL(res.get_count(), t.RXc);
    BOOST_CHECK_EQUAL(write_into_string(buf, res), t.RX);

    res.set(y);

    BOOST_CHECK_EQUAL(res.get_size(), t.RXYs);
    BOOST_CHECK_EQUAL(res.get_count(), t.RXYc);
    BOOST_CHECK_EQUAL(write_into_string(buf, res), t.RXY);
}

bool init_function() {
    int n = sizeof(TestCases) / sizeof(TestCase);
    for(int i = 0; i < n; ++i) {
	boost::unit_test::framework::master_test_suite().
	    add( BOOST_TEST_CASE( boost::bind( &run_test_case, i) ) );
    }
    return true;
}

int main( int argc, char* argv[] ) {
    return ::boost::unit_test::unit_test_main( &init_function, argc, argv );
}
